import { head } from '@inertiajs/react'; // This import is cleaner but usePage is safer usually. Let's stick to standard hooks if possible or just the useEffect.
import { useEffect } from 'react';

export function usePageEffects(title: string) {
    useEffect(() => {
        // Set dynamic document title
        const appName = import.meta.env.VITE_APP_NAME || 'Laravel';
        document.title = title ? `${title} - ${appName}` : appName;

        // Smooth scroll to top on mount/title change
        window.scrollTo({ top: 0, behavior: 'smooth' });
    }, [title]);
}

import { usePage } from '@inertiajs/react';
import type { SharedData, Tenant, Plan } from '@/types';

/**
 * Returns the current authenticated user's tenant data from Inertia shared props.
 * Returns null if the user is not logged in or has no tenant yet.
 */
export function useTenant(): Tenant | null {
    const { tenant } = usePage<SharedData>().props;
    return tenant ?? null;
}

/**
 * Returns feature/limit helpers for the current tenant's plan.
 */
export function usePlanFeatures() {
    const tenant = useTenant();
    const plan = tenant?.plan ?? null;

    return {
        plan,
        isFree: plan?.is_free ?? true,
        isPro: plan?.slug === 'pro',
        hasFeature: (feature: string): boolean => {
            return Boolean(plan?.features?.[feature]);
        },
        getLimit: (key: string): number | null => {
            return (plan?.limits?.[key] as number | null) ?? null;
        },
        withinLimit: (key: string, current: number): boolean => {
            const limit = (plan?.limits?.[key] as number | null) ?? null;
            if (limit === null) return true; // null = unlimited
            return current < limit;
        },
    };
}

/**
 * Returns subscription-related helpers for the current tenant.
 */
export function useSubscription() {
    const tenant = useTenant();

    return {
        isActive: tenant?.subscription_status === 'active',
        isTenant: tenant !== null,
        needsUpgrade: tenant?.plan?.is_free ?? false,
    };
}

/**
 * Returns a Tailwind-compatible CSS var for the tenant's primary color.
 * Use as style prop: style={tenantColor()}
 */
export function useTenantColor(): Record<string, string> {
    const tenant = useTenant();
    return tenant ? { '--color-primary': tenant.primary_color } : {};
}

import type { DragEndEvent } from '@dnd-kit/core';
import { DndContext, closestCenter, KeyboardSensor, PointerSensor, useSensor, useSensors } from '@dnd-kit/core';
import { arrayMove, SortableContext, sortableKeyboardCoordinates, useSortable, rectSortingStrategy } from '@dnd-kit/sortable';
import { CSS } from '@dnd-kit/utilities';
import { Head, useForm, router } from '@inertiajs/react';
import { Pencil, Trash2, Plus, Calendar, Building2, Briefcase } from 'lucide-react';
import { useState } from 'react';
import Swal from 'sweetalert2';
import { Button } from '@/components/ui/button';
import {
    Dialog,
    DialogContent,
    DialogHeader,
    DialogTitle,
    DialogDescription,
    DialogTrigger,
    DialogFooter,
} from "@/components/ui/dialog"
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import AppLayout from '@/layouts/app-layout';
import type { Experience } from '@/types';

// Routes helper (assuming these exist or will use manual paths)
const routes = {
    index: '/dashboard/experiences',
    store: '/dashboard/experiences',
    reorder: '/dashboard/experiences/reorder',
    update: (id: number) => `/dashboard/experiences/${id}`,
    destroy: (id: number) => `/dashboard/experiences/${id}`,
};

const SortableExperienceItem = ({ experience, openEditDialog, handleDelete }: { experience: Experience, openEditDialog: (exp: Experience) => void, handleDelete: (id: number) => void }) => {
    const {
        attributes,
        listeners,
        setNodeRef,
        transform,
        transition,
    } = useSortable({ id: experience.id });

    const style = {
        transform: CSS.Transform.toString(transform),
        transition,
    };

    return (
        <div
            ref={setNodeRef}
            style={style}
            {...attributes}
            {...listeners}
            className="bg-card border border-border rounded-xl p-6 flex flex-col hover:border-cyan-500/30 transition-colors group touch-none"
        >
            <div className="flex items-start justify-between mb-4">
                <div className="p-3 bg-secondary rounded-lg group-hover:bg-cyan-900/20 transition-colors cursor-grab active:cursor-grabbing">
                    <Briefcase className="w-6 h-6 text-cyan-400" />
                </div>
                <div className="flex gap-2">
                    <Button variant="ghost" size="icon" onClick={() => openEditDialog(experience)} className="cursor-pointer" onPointerDown={(e) => e.stopPropagation()}>
                        <Pencil className="w-4 h-4 text-muted-foreground hover:text-cyan-400" />
                    </Button>
                    <Button variant="ghost" size="icon" onClick={() => handleDelete(experience.id)} className="cursor-pointer" onPointerDown={(e) => e.stopPropagation()}>
                        <Trash2 className="w-4 h-4 text-muted-foreground hover:text-red-400" />
                    </Button>
                </div>
            </div>

            <h3 className="text-xl font-bold text-foreground mb-1 line-clamp-1">{experience.role}</h3>

            <div className="flex items-center gap-2 text-muted-foreground text-sm mb-4">
                <Building2 className="w-4 h-4" />
                <span className="line-clamp-1">{experience.company}</span>
            </div>

            <div className="flex items-center gap-2 text-cyan-500 text-xs font-mono mb-4 bg-cyan-950/20 py-1 px-2 rounded w-fit border border-cyan-500/20">
                <Calendar className="w-3 h-3" />
                <span>{experience.period}</span>
            </div>

            <p className="text-muted-foreground text-sm line-clamp-3 mt-auto">
                {experience.description}
            </p>
        </div>
    );
};

const breadcrumbs = [
    {
        title: 'Experiences',
        href: '/dashboard/experiences',
    },
];

export default function ExperienceIndex({ experiences }: { experiences: Experience[] }) {
    const [isDialogOpen, setIsDialogOpen] = useState(false);
    const [editingExperience, setEditingExperience] = useState<Experience | null>(null);
    const [items, setItems] = useState(experiences);

    // Update local state when prop changes
    useState(() => {
        setItems(experiences);
    });

    const sensors = useSensors(
        useSensor(PointerSensor),
        useSensor(KeyboardSensor, {
            coordinateGetter: sortableKeyboardCoordinates,
        })
    );

    const handleDragEnd = (event: DragEndEvent) => {
        const { active, over } = event;

        if (active.id !== over?.id) {
            setItems((items) => {
                const oldIndex = items.findIndex((item) => item.id === active.id);
                const newIndex = items.findIndex((item) => item.id === over?.id);

                const newItems = arrayMove(items, oldIndex, newIndex);

                // Send reorder request
                const orderMap = newItems.map((item, index) => ({
                    id: item.id,
                    order: index
                }));

                router.visit(routes.reorder, {
                    method: 'put',
                    data: { order: orderMap },
                    preserveScroll: true,
                    preserveState: true,
                    onSuccess: () => {
                        // Optional: Show success toast
                    }
                });

                return newItems;
            });
        }
    };

    const { data, setData, post, put, processing, errors, reset, clearErrors } = useForm({
        role: '',
        company: '',
        period: '',
        description: '',
        order: 0,
    });

    const openCreateDialog = () => {
        setEditingExperience(null);
        reset();
        clearErrors();
        setIsDialogOpen(true);
    };

    const openEditDialog = (experience: Experience) => {
        setEditingExperience(experience);
        setData({
            role: experience.role,
            company: experience.company,
            period: experience.period,
            description: experience.description,
            order: experience.order,
        });
        clearErrors();
        setIsDialogOpen(true);
    };

    const handleSubmit = (e: React.FormEvent) => {
        e.preventDefault();

        if (editingExperience) {
            put(routes.update(editingExperience.id), {
                onSuccess: () => {
                    setIsDialogOpen(false);
                    reset();
                    Swal.fire({
                        icon: 'success',
                        title: 'Success',
                        text: 'Experience updated successfully',
                        background: '#1f2937',
                        color: '#fff',
                        confirmButtonColor: '#06b6d4',
                    });
                },
            });
        } else {
            post(routes.store, {
                onSuccess: () => {
                    setIsDialogOpen(false);
                    reset();
                    Swal.fire({
                        icon: 'success',
                        title: 'Success',
                        text: 'Experience created successfully',
                        background: '#1f2937',
                        color: '#fff',
                        confirmButtonColor: '#06b6d4',
                    });
                },
            });
        }
    };

    const handleDelete = (id: number) => {
        Swal.fire({
            title: 'Are you sure?',
            text: "You won't be able to revert this!",
            icon: 'warning',
            showCancelButton: true,
            confirmButtonColor: '#ef4444',
            cancelButtonColor: '#6b7280',
            confirmButtonText: 'Yes, delete it!',
            background: '#1f2937',
            color: '#fff',
        }).then((result) => {
            if (result.isConfirmed) {
                router.delete(routes.destroy(id), {
                    onSuccess: () => {
                        Swal.fire({
                            title: 'Deleted!',
                            text: 'Experience has been deleted.',
                            icon: 'success',
                            background: '#1f2937',
                            color: '#fff',
                            confirmButtonColor: '#06b6d4',
                        });
                    },
                });
            }
        });
    };

    return (
        <AppLayout breadcrumbs={breadcrumbs}>
            <Head title="Manage Experiences" />

            <div className="flex h-full flex-1 flex-col gap-4 p-4">
                <div className="flex items-center justify-between mb-4">
                    <div>
                        <h1 className="text-2xl font-bold text-foreground">Manage Experiences</h1>
                        <p className="text-muted-foreground mt-1">Add, edit, or remove your professional experiences.</p>
                    </div>
                    <Button onClick={openCreateDialog} className="bg-primary hover:bg-primary/90 text-primary-foreground">
                        <Plus className="w-4 h-4 mr-2" />
                        Add New
                    </Button>
                </div>

                <DndContext
                    sensors={sensors}
                    collisionDetection={closestCenter}
                    onDragEnd={handleDragEnd}
                >
                    <SortableContext
                        items={items}
                        strategy={rectSortingStrategy}
                    >
                        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                            {items.map((experience) => (
                                <SortableExperienceItem
                                    key={experience.id}
                                    experience={experience}
                                    openEditDialog={openEditDialog}
                                    handleDelete={handleDelete}
                                />
                            ))}
                        </div>
                    </SortableContext>
                </DndContext>

                {experiences.length === 0 && (
                    <div className="text-center py-20 border border-dashed border-border rounded-xl">
                        <Briefcase className="h-12 w-12 text-muted-foreground mx-auto mb-4" />
                        <h3 className="text-xl font-bold text-foreground mb-2">No experiences yet</h3>
                        <p className="text-muted-foreground">Get started by adding your first professional experience.</p>
                    </div>
                )}
            </div>

            {/* Create/Edit Dialog */}
            <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
                <DialogContent className="bg-card border-border text-foreground sm:max-w-[500px]">
                    <DialogHeader>
                        <DialogTitle className="text-foreground">{editingExperience ? 'Edit Experience' : 'Add New Experience'}</DialogTitle>
                        <DialogDescription className="sr-only">
                            {editingExperience ? 'Edit the details of your professional experience.' : 'Add a new professional experience to your portfolio.'}
                        </DialogDescription>
                    </DialogHeader>
                    <form onSubmit={handleSubmit} className="space-y-4 mt-4">
                        <div className="space-y-2">
                            <Label htmlFor="role" className="text-foreground">Role / Job Title</Label>
                            <Input
                                id="role"
                                value={data.role}
                                onChange={(e) => setData('role', e.target.value)}
                                placeholder="e.g. Senior Frontend Developer"
                                className="bg-background border-input focus:border-cyan-500 text-foreground"
                                required
                            />
                            {errors.role && <p className="text-red-500 text-xs">{errors.role}</p>}
                        </div>

                        <div className="space-y-2">
                            <Label htmlFor="company" className="text-foreground">Company Name</Label>
                            <Input
                                id="company"
                                value={data.company}
                                onChange={(e) => setData('company', e.target.value)}
                                placeholder="e.g. Google Inc."
                                className="bg-background border-input focus:border-cyan-500 text-foreground"
                                required
                            />
                            {errors.company && <p className="text-red-500 text-xs">{errors.company}</p>}
                        </div>

                        <div className="space-y-2">
                            <Label htmlFor="period" className="text-foreground">Period</Label>
                            <Input
                                id="period"
                                value={data.period}
                                onChange={(e) => setData('period', e.target.value)}
                                placeholder="e.g. Jan 2023 - Present"
                                className="bg-background border-input focus:border-cyan-500 text-foreground"
                                required
                            />
                            {errors.period && <p className="text-red-500 text-xs">{errors.period}</p>}
                        </div>

                        <div className="space-y-2">
                            <Label htmlFor="description" className="text-foreground">Description</Label>
                            <Textarea
                                id="description"
                                value={data.description}
                                onChange={(e) => setData('description', e.target.value)}
                                placeholder="Describe your responsibilities and achievements..."
                                className="bg-background border-input focus:border-cyan-500 min-h-[100px] text-foreground"
                                required
                            />
                            {errors.description && <p className="text-red-500 text-xs">{errors.description}</p>}
                        </div>

                        <DialogFooter className="mt-6">
                            <Button type="button" variant="ghost" onClick={() => setIsDialogOpen(false)} className="mr-2 text-muted-foreground hover:text-foreground">
                                Cancel
                            </Button>
                            <Button type="submit" className="bg-primary hover:bg-primary/90 text-primary-foreground" disabled={processing}>
                                {processing ? 'Saving...' : (editingExperience ? 'Update Experience' : 'Create Experience')}
                            </Button>
                        </DialogFooter>
                    </form>
                </DialogContent>
            </Dialog>
        </AppLayout>
    );
}

import type { DragEndEvent } from '@dnd-kit/core';
import { DndContext, closestCenter, KeyboardSensor, PointerSensor, useSensor, useSensors } from '@dnd-kit/core';
import { arrayMove, SortableContext, sortableKeyboardCoordinates, verticalListSortingStrategy } from '@dnd-kit/sortable';
import { useSortable } from '@dnd-kit/sortable';
import { CSS } from '@dnd-kit/utilities';
import { Head, Link, router } from '@inertiajs/react';
import { Eye, GripVertical, Pencil, Trash2 } from 'lucide-react';
import { useState } from 'react';
import Swal from 'sweetalert2';
import { Button, buttonVariants } from '@/components/ui/button';
import AppLayout from '@/layouts/app-layout';
import { index, create, show, edit, destroy } from '@/routes/dashboard/portfolios';
import type { BreadcrumbItem } from '@/types';

const breadcrumbs: BreadcrumbItem[] = [
    { title: 'Dashboard', href: '/dashboard' },
    { title: 'Portfolios', href: '/dashboard/portfolios' },
];

type PortfolioImage = {
    id: number;
    image_path: string;
    order: number;
};

type Portfolio = {
    id: number;
    title: string;
    category: string;
    description: string;
    project_url: string;
    image_path: string;
    images: PortfolioImage[];
    order: number;
};

type Props = {
    portfolios: Portfolio[];
    can_add_portfolio: boolean;
    max_portfolios: number | null;
    current_count: number;
};

// Sortable Portfolio Card Component
function SortablePortfolioCard({ portfolio, onDelete }: { portfolio: Portfolio; onDelete: (id: number) => void }) {
    const {
        attributes,
        listeners,
        setNodeRef,
        transform,
        transition,
        isDragging,
    } = useSortable({ id: portfolio.id });

    const style = {
        transform: CSS.Transform.toString(transform),
        transition,
        opacity: isDragging ? 0.5 : 1,
    };

    const handleCardClick = (e: React.MouseEvent) => {
        // Prevent navigation if clicking on buttons or drag handle
        if ((e.target as HTMLElement).closest('button') || (e.target as HTMLElement).closest('[data-drag-handle]')) {
            return;
        }
        router.visit(show.url({ portfolio: portfolio.id }));
    };

    return (
        <div
            ref={setNodeRef}
            style={style}
            className="group relative flex flex-col rounded-lg border border-white/10 bg-white/5 backdrop-blur-md overflow-hidden transition-all hover:border-cyan-500/50 hover:shadow-lg cursor-pointer"
            onClick={handleCardClick}
        >
            {/* Drag Handle */}
            <div
                {...attributes}
                {...listeners}
                data-drag-handle
                className="absolute top-2 left-2 z-10 cursor-grab active:cursor-grabbing bg-gray-800/80 rounded p-1 opacity-0 group-hover:opacity-100 transition-opacity"
            >
                <GripVertical className="h-5 w-5 text-gray-400" />
            </div>

            {/* Thumbnail */}
            {(portfolio.image_path || portfolio.images?.length > 0) && (
                <div className="aspect-video w-full overflow-hidden bg-gray-800">
                    <img
                        src={`/storage/${portfolio.image_path || portfolio.images[0]?.image_path}`}
                        alt={portfolio.title}
                        className="h-full w-full object-cover transition-transform duration-300 group-hover:scale-110"
                    />
                </div>
            )}

            {/* Content */}
            <div className="flex flex-col flex-1 p-4">
                <div className="flex items-start justify-between mb-2">
                    <div className="flex-1">
                        <h2 className="font-semibold text-white text-lg line-clamp-1">{portfolio.title}</h2>
                        <p className="text-sm text-cyan-400">{portfolio.category}</p>
                    </div>
                </div>

                {portfolio.images?.length > 0 && (
                    <p className="text-xs text-gray-500 mb-3">
                        {portfolio.images.length} gambar tambahan
                    </p>
                )}

                {/* Action Buttons */}
                <div className="flex items-center gap-2 mt-auto pt-3 border-t border-gray-800">
                    <Link
                        href={show.url({ portfolio: portfolio.id })}
                        className={buttonVariants({ variant: 'outline', size: 'sm', className: 'flex-1' })}
                        onClick={(e) => e.stopPropagation()}
                    >
                        <Eye className="h-4 w-4 mr-1" />
                        Detail
                    </Link>
                    <Link
                        href={edit.url({ portfolio: portfolio.id })}
                        className={buttonVariants({ variant: 'outline', size: 'sm', className: 'flex-1' })}
                        onClick={(e) => e.stopPropagation()}
                    >
                        <Pencil className="h-4 w-4 mr-1" />
                        Edit
                    </Link>
                    <Button
                        variant="destructive"
                        size="sm"
                        onClick={(e) => {
                            e.stopPropagation();
                            onDelete(portfolio.id);
                        }}
                        className="flex-1"
                    >
                        <Trash2 className="h-4 w-4 mr-1" />
                        Hapus
                    </Button>
                </div>
            </div>
        </div>
    );
}

export default function PortfolioIndex({ portfolios: initialPortfolios, can_add_portfolio, max_portfolios, current_count }: Props) {
    const [portfolios, setPortfolios] = useState(initialPortfolios);

    const sensors = useSensors(
        useSensor(PointerSensor),
        useSensor(KeyboardSensor, {
            coordinateGetter: sortableKeyboardCoordinates,
        })
    );

    const handleDragEnd = (event: DragEndEvent) => {
        const { active, over } = event;

        if (over && active.id !== over.id) {
            setPortfolios((items) => {
                const oldIndex = items.findIndex((item) => item.id === active.id);
                const newIndex = items.findIndex((item) => item.id === over.id);

                const newItems = arrayMove(items, oldIndex, newIndex);

                // Update order on server
                const orderData = newItems.map((item, index) => ({
                    id: item.id,
                    order: index,
                }));

                router.post('/dashboard/portfolios/reorder', { portfolios: orderData }, {
                    preserveScroll: true,
                    preserveState: true,
                });

                return newItems;
            });
        }
    };

    const handleDelete = (id: number) => {
        Swal.fire({
            title: 'Apakah Anda yakin?',
            text: 'Portfolio ini akan dihapus permanen!',
            icon: 'warning',
            showCancelButton: true,
            confirmButtonColor: '#ef4444',
            cancelButtonColor: '#6b7280',
            confirmButtonText: 'Ya, hapus!',
            cancelButtonText: 'Batal',
            background: '#1f2937',
            color: '#fff',
        }).then((result) => {
            if (result.isConfirmed) {
                router.delete(destroy.url({ portfolio: id }), {
                    onSuccess: () => {
                        Swal.fire({
                            title: 'Terhapus!',
                            text: 'Portfolio berhasil dihapus.',
                            icon: 'success',
                            background: '#1f2937',
                            color: '#fff',
                            confirmButtonColor: '#06b6d4',
                        });
                    },
                    onError: (errors) => {
                        Swal.fire({
                            title: 'Gagal!',
                            text: 'Terjadi kesalahan saat menghapus portfolio.',
                            icon: 'error',
                            background: '#1f2937',
                            color: '#fff',
                            confirmButtonColor: '#ef4444',
                        });
                        console.error('Delete error:', errors);
                    },
                });
            }
        });
    };

    return (
        <AppLayout breadcrumbs={breadcrumbs}>
            <Head title="Manajemen Portofolio" />

            <div className="w-full max-w-7xl mx-auto px-4 sm:px-6 md:px-8 py-10">
                <div className="flex items-center justify-between mb-8">
                    <div>
                        <h1 className="text-3xl font-bold text-white">Manajemen Portofolio</h1>
                        <p className="text-gray-400 mt-1">Kelola dan atur urutan portfolio Anda</p>
                    </div>
                    {can_add_portfolio ? (
                        <Link href={create.url()} className={buttonVariants()}>
                            Tambah Baru
                        </Link>
                    ) : (
                        <Button disabled className="opacity-50 cursor-not-allowed hidden sm:inline-flex">
                            Maksimal {max_portfolios} Item
                        </Button>
                    )}
                </div>

                {!can_add_portfolio && max_portfolios !== null && (
                    <div className="mb-8 p-4 bg-orange-500/10 border border-orange-500/30 rounded-lg flex flex-col sm:flex-row items-start sm:items-center justify-between gap-4">
                        <div>
                            <h3 className="font-semibold text-orange-400">Batas Kuota Portofolio Tercapai ({current_count}/{max_portfolios})</h3>
                            <p className="text-sm text-gray-300 mt-1">Anda menggunakan paket Free yang hanya mengizinkan maksimal {max_portfolios} portofolio. Upgrade ke paket Pro untuk membuat item baru.</p>
                        </div>
                        <Link href="/dashboard/billing" className={buttonVariants({ variant: 'default', size: 'sm', className: 'whitespace-nowrap shrink-0 bg-orange-600 hover:bg-orange-700 text-white' })}>
                            Upgrade ke Pro
                        </Link>
                    </div>
                )}

                {portfolios.length > 0 ? (
                    <DndContext
                        sensors={sensors}
                        collisionDetection={closestCenter}
                        onDragEnd={handleDragEnd}
                    >
                        <SortableContext
                            items={portfolios.map((p) => p.id)}
                            strategy={verticalListSortingStrategy}
                        >
                            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                                {portfolios.map((portfolio) => (
                                    <SortablePortfolioCard
                                        key={portfolio.id}
                                        portfolio={portfolio}
                                        onDelete={handleDelete}
                                    />
                                ))}
                            </div>
                        </SortableContext>
                    </DndContext>
                ) : (
                    <div className="text-center py-16 border border-dashed border-gray-800 rounded-xl">
                        <p className="text-gray-400 text-lg">Belum ada item portofolio.</p>
                        <p className="text-gray-500 mt-2">Mulai dengan membuat yang baru.</p>
                    </div>
                )}
            </div>
        </AppLayout>
    );
}

import { Head, useForm, usePage } from '@inertiajs/react';
import { motion } from 'framer-motion';
import { Plus, Trash2, Save, Wrench, Share2, Loader2, Code } from 'lucide-react';
import type { FormEvent} from 'react';
import { useEffect } from 'react';
import Swal from 'sweetalert2';
import InputError from '@/components/input-error';
import { Button } from '@/components/ui/button';
import { Card, CardHeader, CardContent } from '@/components/ui/card';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Separator } from '@/components/ui/separator';
import AppLayout from '@/layouts/app-layout';
import type { PageProps, BreadcrumbItem } from '@/types';
import type { Skill, SocialLink} from '@/types/profile';
import { UserProfile } from '@/types/profile';

// eslint-disable-next-line @typescript-eslint/no-explicit-any
declare let route: any;

const breadcrumbs: BreadcrumbItem[] = [
    {
        title: 'Skills & Tools',
        href: '/dashboard/skills',
    },
];

interface SkillsPageProps extends PageProps {
    skills: Skill[];
    soft_skills: SoftSkill[];
    social_links: SocialLink[];
}

interface SoftSkill {
    name: string;
    icon?: string;
}

export default function SkillsManager({ skills, soft_skills, social_links }: SkillsPageProps) {
    const { data, setData, patch, processing, errors, recentlySuccessful, transform } = useForm({
        skills: skills || [],
        soft_skills: soft_skills || [],
        social_links: social_links || [],
    });

    // Clean empty social links before submission
    transform((data) => ({
        ...data,
        social_links: data.social_links.filter(link => link.url && link.url.trim() !== '')
    }));

    const { flash } = usePage<PageProps>().props;

    useEffect(() => {
        if (recentlySuccessful) {
            const Toast = Swal.mixin({
                toast: true,
                position: 'top-end',
                showConfirmButton: false,
                timer: 3000,
                timerProgressBar: true,
                didOpen: (toast) => {
                    toast.onmouseenter = Swal.stopTimer;
                    toast.onmouseleave = Swal.resumeTimer;
                }
            });

            Toast.fire({
                icon: 'success',
                title: 'Skills & Social Links saved successfully'
            });
        }
    }, [recentlySuccessful]);

    // Helper for success toasts
    const showSuccessToast = (message: string) => {
        Swal.mixin({
            toast: true,
            position: 'top-end',
            showConfirmButton: false,
            timer: 3000,
            timerProgressBar: true,
            didOpen: (toast) => {
                toast.onmouseenter = Swal.stopTimer;
                toast.onmouseleave = Swal.resumeTimer;
            }
        }).fire({
            icon: 'success',
            title: message
        });
    };

    const submit = (e: FormEvent) => {
        e.preventDefault();

        // Data transformation is handled by the `transform` callback above
        patch(route('dashboard.skills.update'), {
            preserveScroll: true,
            onError: (errors) => {
                const errorMessages = Object.values(errors).flat().join('\n');
                Swal.fire({
                    icon: 'error',
                    title: 'Submission Failed',
                    text: errorMessages || 'Please check the form for errors.',
                    confirmButtonColor: '#ef4444',
                });
            }
        });
    };

    // Skills Handlers


    const removeSkill = (index: number) => {
        const newSkills = [...data.skills];
        newSkills.splice(index, 1);
        setData('skills', newSkills);
    };

    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    const updateSkill = (index: number, key: keyof Skill, value: any) => {
        const newSkills = [...data.skills];
        newSkills[index] = { ...newSkills[index], [key]: value };
        setData('skills', newSkills);
    };
    // Soft Skills Handlers
    const addSoftSkill = () => {
        setData('soft_skills', [
            ...data.soft_skills,
            { name: '', icon: '' } // Default empty soft skill
        ]);
    };

    const removeSoftSkill = (index: number) => {
        const newSoftSkills = data.soft_skills.filter((_, i) => i !== index);
        setData('soft_skills', newSoftSkills);
    };

    const updateSoftSkill = (index: number, field: keyof SoftSkill, value: string) => {
        const newSoftSkills = [...data.soft_skills];
        newSoftSkills[index] = { ...newSoftSkills[index], [field]: value };
        setData('soft_skills', newSoftSkills);
    };

    // Social Links Handlers
    const addSocialLink = () => {
        setData('social_links', [
            ...data.social_links,
            { platform: 'GitHub', url: '' },
        ]);
    };

    const removeSocialLink = (index: number) => {
        const newLinks = [...data.social_links];
        newLinks.splice(index, 1);
        setData('social_links', newLinks);
    };

    const updateSocialLink = (index: number, key: keyof SocialLink, value: string) => {
        const newLinks = [...data.social_links];
        newLinks[index] = { ...newLinks[index], [key]: value };
        setData('social_links', newLinks);
    };

    return (
        <AppLayout breadcrumbs={breadcrumbs}>
            <Head title="Skills & Tools" />

            <div className="p-6 max-w-7xl mx-auto pb-24">
                <form onSubmit={submit}>
                    {/* Header Actions */}
                    <div className="flex flex-col sm:flex-row items-start sm:items-center justify-between gap-4 mb-10">
                        <div>
                            <h1 className="text-3xl font-bold tracking-tight text-foreground">Skills & Tools</h1>
                            <p className="text-muted-foreground mt-1">
                                Manage your technical expertise, soft skills, and social presence.
                            </p>
                        </div>
                        <Button 
                            type="submit" 
                            disabled={processing} 
                            size="lg"
                            className="shadow-lg shadow-primary/20"
                        >
                            {processing ? (
                                <>
                                    <Loader2 className="w-5 h-5 mr-2 animate-spin" />
                                    Saving...
                                </>
                            ) : (
                                <>
                                    <Save className="w-5 h-5 mr-2" />
                                    Save Changes
                                </>
                            )}
                        </Button>
                    </div>

                    <div className="space-y-12">
                        {/* SECTION 1: TECHNICAL SKILLS */}
                        <section>
                            <div className="flex items-center justify-between mb-6">
                                <div className="flex items-center gap-3">
                                    <div className="p-3 bg-blue-500/10 rounded-xl">
                                        <Wrench className="w-6 h-6 text-blue-600 dark:text-blue-400" />
                                    </div>
                                    <div>
                                        <h2 className="text-xl font-bold">Technical Skills</h2>
                                        <p className="text-sm text-muted-foreground">Grouped by category (e.g. Backend, Frontend)</p>
                                    </div>
                                </div>
                                <Button type="button" variant="secondary" onClick={() => {
                                    Swal.fire({
                                        title: 'New Category Name',
                                        input: 'text',
                                        inputPlaceholder: 'e.g., Cloud Services',
                                        showCancelButton: true,
                                        confirmButtonText: 'Create',
                                    }).then((result) => {
                                        if (result.isConfirmed && result.value) {
                                            setData('skills', [
                                                ...data.skills,
                                                { name: '', level: 50, category: result.value }
                                            ]);
                                            showSuccessToast(`Category "${result.value}" created`);
                                        }
                                    });
                                }}>
                                    <Plus className="w-4 h-4 mr-2" />
                                    Add Category
                                </Button>
                            </div>

                            {data.skills.length === 0 && (
                                <div className="text-center py-16 border-2 border-dashed rounded-xl bg-card/50 text-muted-foreground">
                                    <p>No technical skills added yet.</p>
                                    <p className="text-sm mt-1">Create a category to get started.</p>
                                </div>
                            )}

                            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                                {Object.entries(data.skills.reduce((acc, skill, index) => {
                                    if (!acc[skill.category]) acc[skill.category] = [];
                                    acc[skill.category].push({ skill, index });
                                    return acc;
                                }, {} as Record<string, { skill: Skill; index: number }[]>)).map(([category, categoryItems]) => (
                                    <Card key={category} className="shadow-sm hover:shadow-md transition-shadow">
                                        <CardHeader className="flex flex-row items-center justify-between pb-2 space-y-0">
                                            <h3 className="font-semibold text-lg truncate" title={category}>{category}</h3>
                                            <div className="flex items-center gap-1">
                                                <Button
                                                    type="button"
                                                    variant="ghost"
                                                    size="icon"
                                                    className="h-8 w-8 text-muted-foreground hover:text-foreground"
                                                    onClick={() => {
                                                         Swal.fire({
                                                            title: 'Rename Category',
                                                            input: 'text',
                                                            inputValue: category,
                                                            showCancelButton: true,
                                                            confirmButtonText: 'Rename',
                                                        }).then((result) => {
                                                            if (result.isConfirmed && result.value && result.value !== category) {
                                                                const newSkills = data.skills.map(s => 
                                                                    s.category === category ? { ...s, category: result.value } : s
                                                                );
                                                                setData('skills', newSkills);
                                                                showSuccessToast(`Renamed to "${result.value}"`);
                                                            }
                                                        });
                                                    }}
                                                >
                                                    <Wrench className="w-3.5 h-3.5" />
                                                </Button>
                                                <Button
                                                    type="button"
                                                    variant="ghost"
                                                    size="icon"
                                                    className="h-8 w-8 text-destructive hover:bg-destructive/10"
                                                    onClick={() => {
                                                        Swal.fire({
                                                            title: 'Delete Category?',
                                                            text: `Remove "${category}" and all its skills?`,
                                                            icon: 'warning',
                                                            showCancelButton: true,
                                                            confirmButtonColor: '#ef4444',
                                                            confirmButtonText: 'Delete'
                                                        }).then((result) => {
                                                            if (result.isConfirmed) {
                                                                const newSkills = data.skills.filter(s => s.category !== category);
                                                                setData('skills', newSkills);
                                                                showSuccessToast('Category deleted');
                                                            }
                                                        });
                                                    }}
                                                >
                                                    <Trash2 className="w-3.5 h-3.5" />
                                                </Button>
                                            </div>
                                        </CardHeader>
                                        <CardContent className="space-y-4">
                                            {categoryItems.map(({ skill, index: realIndex }) => (
                                                <div key={realIndex} className="space-y-3 p-3 bg-secondary/30 rounded-lg group">
                                                    <div className="flex items-start gap-3">
                                                        <div className="flex-1 space-y-1">
                                                            <Input
                                                                value={skill.name}
                                                                onChange={(e) => updateSkill(realIndex, 'name', e.target.value)}
                                                                placeholder="Skill Name (e.g. React)"
                                                                className="h-8 text-sm"
                                                            />

                                                            <InputError message={errors[`skills.${realIndex}.name`]} />
                                                        </div>
                                                        <Button
                                                            type="button"
                                                            variant="ghost"
                                                            size="icon"
                                                            className="h-8 w-8 text-muted-foreground opacity-50 group-hover:opacity-100 transition-opacity hover:text-destructive"
                                                            onClick={() => removeSkill(realIndex)}
                                                        >
                                                            <Trash2 className="w-3.5 h-3.5" />
                                                        </Button>
                                                    </div>
                                                    <div className="space-y-1.5">
                                                        <div className="flex justify-between text-xs text-muted-foreground">
                                                            <span>Proficiency</span>
                                                            <span>{skill.level}%</span>
                                                        </div>
                                                        <input
                                                            type="range"
                                                            min="1"
                                                            max="100"
                                                            className="w-full h-1.5 bg-secondary rounded-full appearance-none cursor-pointer accent-primary"
                                                            value={skill.level}
                                                            onChange={(e) => updateSkill(realIndex, 'level', parseInt(e.target.value))}
                                                        />
                                                    </div>
                                                </div>
                                            ))}
                                            <Button 
                                                type="button" 
                                                variant="outline" 
                                                size="sm" 
                                                className="w-full border-dashed text-xs"
                                                onClick={() => setData('skills', [...data.skills, { name: '', level: 50, category }])}
                                            >
                                                <Plus className="w-3.5 h-3.5 mr-1.5" />
                                                Add Skill
                                            </Button>
                                        </CardContent>
                                    </Card>
                                ))}
                            </div>
                        </section>

                        <Separator />

                        {/* SECTION 2: SOFT SKILLS */}
                        <section>
                             <div className="flex items-center justify-between mb-6">
                                <div className="flex items-center gap-3">
                                    <div className="p-3 bg-purple-500/10 rounded-xl">
                                        <Code className="w-6 h-6 text-purple-600 dark:text-purple-400" />
                                    </div>
                                    <div>
                                        <h2 className="text-xl font-bold">Soft Skills</h2>
                                        <p className="text-sm text-muted-foreground">Personal attributes & interpersonal skills</p>
                                    </div>
                                </div>
                                <Button type="button" variant="secondary" onClick={addSoftSkill}>
                                    <Plus className="w-4 h-4 mr-2" />
                                    Add Soft Skill
                                </Button>
                            </div>

                            <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
                                {data.soft_skills.map((skill, index) => (
                                    <motion.div
                                        key={index}
                                        layout
                                        initial={{ opacity: 0, scale: 0.95 }}
                                        animate={{ opacity: 1, scale: 1 }}
                                        className="bg-card border rounded-xl p-4 shadow-sm group hover:border-purple-500/50 transition-colors"
                                    >
                                        <div className="flex items-start justify-between gap-2 mb-3">
                                            <div className="p-2 bg-purple-50 dark:bg-purple-900/20 rounded-lg text-lg">
                                                {skill.icon || ''}
                                            </div>
                                            <Button
                                                type="button"
                                                variant="ghost"
                                                size="icon"
                                                className="h-6 w-6 text-muted-foreground hover:text-destructive opacity-0 group-hover:opacity-100 transition-opacity"
                                                onClick={() => removeSoftSkill(index)}
                                            >
                                                <Trash2 className="w-3.5 h-3.5" />
                                            </Button>
                                        </div>
                                        <div className="space-y-3">
                                            <div className="space-y-1">
                                                <Label className="text-xs text-muted-foreground">Skill Name</Label>
                                                <Input
                                                    value={skill.name}
                                                    onChange={(e) => updateSoftSkill(index, 'name', e.target.value)}
                                                    placeholder="e.g. Leadership"
                                                    className="h-8 text-sm"
                                                />
                                            </div>
                                            <div className="space-y-1">
                                                <Label className="text-xs text-muted-foreground">Emoji Icon</Label>
                                                <Input
                                                    value={skill.icon || ''}
                                                    onChange={(e) => updateSoftSkill(index, 'icon', e.target.value)}
                                                    placeholder="e.g. "
                                                    className="h-8 text-sm"
                                                />
                                            </div>
                                        </div>
                                    </motion.div>
                                ))}
                                {data.soft_skills.length === 0 && (
                                     <div className="col-span-full text-center py-12 border-2 border-dashed rounded-xl bg-card/50 text-muted-foreground">
                                        <p>No soft skills yet.</p>
                                    </div>
                                )}
                            </div>
                        </section>

                        <Separator />

                        {/* SECTION 3: SOCIAL LINKS */}
                        <section>
                             <div className="flex items-center justify-between mb-6">
                                <div className="flex items-center gap-3">
                                    <div className="p-3 bg-cyan-500/10 rounded-xl">
                                        <Share2 className="w-6 h-6 text-cyan-600 dark:text-cyan-400" />
                                    </div>
                                    <div>
                                        <h2 className="text-xl font-bold">Social Links</h2>
                                        <p className="text-sm text-muted-foreground">Your presence across the web</p>
                                    </div>
                                </div>
                                <Button type="button" variant="secondary" onClick={addSocialLink}>
                                    <Plus className="w-4 h-4 mr-2" />
                                    Add Link
                                </Button>
                            </div>

                            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4">
                                {data.social_links.map((link, index) => (
                                    <div key={index} className="flex items-center gap-3 p-3 border rounded-xl bg-card shadow-sm group hover:border-cyan-500/50 transition-colors">
                                        <div className="cursor-move text-muted-foreground hover:text-foreground">
                                            {/* We could add drag handle here if using dnd, for now just an icon */}
                                            <Share2 className="w-4 h-4 opacity-50" />
                                        </div>
                                        <div className="flex-1 space-y-2">
                                            <div className="grid grid-cols-3 gap-2">
                                                <select
                                                    className="col-span-1 h-8 rounded-md border border-input bg-background px-2 text-xs font-medium shadow-sm focus-visible:outline-none focus-visible:ring-1 focus-visible:ring-ring"
                                                    value={link.platform}
                                                    onChange={(e) => updateSocialLink(index, 'platform', e.target.value)}
                                                >
                                                    <option value="GitHub">GitHub</option>
                                                    <option value="LinkedIn">LinkedIn</option>
                                                    <option value="Twitter">Twitter</option>
                                                    <option value="Instagram">Instagram</option>
                                                    <option value="YouTube">YouTube</option>
                                                    <option value="Facebook">Facebook</option>
                                                    <option value="Email">Email</option>
                                                    <option value="Website">Website</option>
                                                </select>
                                                <Input
                                                    className="col-span-2 h-8 text-xs"
                                                    placeholder="URL / Handle"
                                                    value={link.url}
                                                    onChange={(e) => updateSocialLink(index, 'url', e.target.value)}
                                                    onBlur={(e) => {
                                                        const val = e.target.value.trim();
                                                        if (val && !val.match(/^(https?:\/\/|mailto:)/)) {
                                                            updateSocialLink(index, 'url', 'https://' + val);
                                                        }
                                                    }}
                                                />
                                            </div>
                                        </div>
                                        <Button
                                            type="button"
                                            variant="ghost"
                                            size="icon"
                                            className="h-8 w-8 text-muted-foreground hover:text-destructive hover:bg-destructive/10"
                                            onClick={() => removeSocialLink(index)}
                                        >
                                            <Trash2 className="w-4 h-4" />
                                        </Button>
                                    </div>
                                ))}
                                {data.social_links.length === 0 && (
                                     <div className="col-span-full text-center py-12 border-2 border-dashed rounded-xl bg-card/50 text-muted-foreground">
                                        <p>No social links added.</p>
                                    </div>
                                )}
                            </div>
                             {/* @ts-expect-error */}
                             {errors.social_links && <InputError message={errors.social_links} className="mt-4" />}
                        </section>
                    </div>
                </form>
            </div>
        </AppLayout>
    );
}

import { Deferred, Head, Link, usePage } from '@inertiajs/react';
import { PlusCircle, Eye, MousePointer, TrendingUp, ExternalLink, Zap, Globe, Sparkles } from 'lucide-react';
import { Line, LineChart, ResponsiveContainer, Tooltip as RechartsTooltip, XAxis, YAxis, CartesianGrid } from 'recharts';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { usePageEffects } from '@/hooks/use-page-effects';
import { useTenant, usePlanFeatures } from '@/hooks/use-tenant';
import AppLayout from '@/layouts/app-layout';
import dashboard from '@/routes/dashboard';
import type { BreadcrumbItem, SharedData } from '@/types';

const breadcrumbs: BreadcrumbItem[] = [
    {
        title: 'Dashboard',
        href: dashboard.index.url(),
    },
];

type Statistics = {
    total_home_views: number;
    total_portfolio_views: number;
    portfolio_stats: {
        id: number;
        title: string;
        views: number;
    }[];
    daily_views: {
        date: string;
        views: number;
    }[];
};

interface DashboardProps extends SharedData {
    statistics?: Statistics;
}

export default function Dashboard({ statistics }: DashboardProps) {
    usePageEffects('Dashboard');
    const { auth } = usePage<SharedData>().props;
    const tenant = useTenant();
    const { isFree, plan } = usePlanFeatures();

    const publicPortfolioUrl = tenant?.slug ? `/${tenant.slug}` : '/';

    return (
        <AppLayout breadcrumbs={breadcrumbs}>
            <Head title="Dashboard" />
            <div className="flex h-full flex-1 flex-col gap-4 overflow-x-hidden rounded-xl p-4 md:p-8 lg:p-12">

                {/* Plan Banner  Free tier upgrade prompt */}
                {isFree && (
                    <div className="from-primary/10 to-purple-500/10 border-primary/20 flex flex-col items-start justify-between gap-3 rounded-xl border bg-gradient-to-r p-4 sm:flex-row sm:items-center">
                        <div className="flex items-center gap-3">
                            <div className="bg-primary/20 rounded-lg p-2">
                                <Sparkles className="text-primary h-5 w-5" />
                            </div>
                            <div>
                                <p className="text-sm font-semibold">Anda menggunakan <span className="text-primary">Free Plan</span></p>
                                <p className="text-muted-foreground text-xs">Upgrade ke Pro untuk portfolio tak terbatas, custom domain & analytics.</p>
                            </div>
                        </div>
                        <Button size="sm" className="w-full shrink-0 sm:w-auto">
                            <Zap className="mr-1 h-3.5 w-3.5" />
                            Upgrade ke Pro
                        </Button>
                    </div>
                )}

                {/* Welcome Card */}
                <Card className="glass text-foreground transition-all duration-300 hover:scale-[1.02]">
                    <CardHeader>
                        <div className="flex flex-wrap items-start justify-between gap-2">
                            <CardTitle className="text-foreground">Selamat Datang, {auth.user.name}!</CardTitle>
                            {plan && (
                                <span className={`inline-flex items-center rounded-full px-2.5 py-0.5 text-xs font-semibold ${isFree ? 'bg-secondary text-secondary-foreground' : 'bg-primary/20 text-primary'}`}>
                                    {plan.name}
                                </span>
                            )}
                        </div>
                        <CardDescription className="text-muted-foreground">
                            Pusat kendali portfolio Anda.
                            {tenant?.slug && (
                                <span className="text-primary ml-1">
                                    URL Anda: <a href={publicPortfolioUrl} target="_blank" rel="noopener noreferrer" className="hover:underline">portfolify.app/{tenant.slug}</a>
                                </span>
                            )}
                        </CardDescription>
                    </CardHeader>
                    <CardContent>
                        <div className="mt-1 flex flex-col gap-3 sm:flex-row">
                            <Button asChild className="bg-primary hover:bg-primary/90 text-primary-foreground w-full border-0 sm:w-auto">
                                <Link href={dashboard.portfolios.index.url()}>
                                    <PlusCircle className="mr-2 h-4 w-4" />
                                    Kelola Portofolio
                                </Link>
                            </Button>
                            <Button asChild variant="outline" className="border-border hover:bg-accent hover:text-accent-foreground w-full sm:w-auto">
                                <a href={publicPortfolioUrl} target="_blank" rel="noopener noreferrer">
                                    <Globe className="mr-2 h-4 w-4" />
                                    Lihat Portfolio Publik
                                </a>
                            </Button>
                        </div>
                    </CardContent>
                </Card>

                {/* Statistics Section using Deferred Props */}
                <Deferred data="statistics" fallback={
                   <div className="grid grid-cols-2 gap-4 md:grid-cols-2 lg:grid-cols-3">
                        {[1, 2, 3].map((i) => (
                             <Card key={i} className={`glass h-32 animate-pulse ${i === 3 ? 'col-span-2 md:col-span-1' : ''}`} />
                        ))}
                   </div>
                }>
                    {statistics && (
                        <>
                            {/* Analytics Chart */}
                            <Card className="glass text-foreground mb-4">
                                <CardHeader>
                                    <CardTitle>Trend Kunjungan (30 Hari Terakhir)</CardTitle>
                                    <CardDescription>Aktivitas penayangan portofolio Anda</CardDescription>
                                </CardHeader>
                                <CardContent>
                                    <div className="w-full mt-4" style={{ minHeight: '300px' }}>
                                        <ResponsiveContainer width="100%" height={300}>
                                            <LineChart data={statistics.daily_views} margin={{ top: 5, right: 10, left: -20, bottom: 0 }}>
                                                <CartesianGrid strokeDasharray="3 3" vertical={false} stroke="#3f3f46" />
                                                <XAxis 
                                                    dataKey="date" 
                                                    axisLine={false}
                                                    tickLine={false}
                                                    tick={{ fill: '#a1a1aa', fontSize: 12 }}
                                                    dy={10}
                                                />
                                                <YAxis 
                                                    axisLine={false}
                                                    tickLine={false}
                                                    tick={{ fill: '#a1a1aa', fontSize: 12 }}
                                                    allowDecimals={false}
                                                />
                                                <RechartsTooltip 
                                                    contentStyle={{ 
                                                        backgroundColor: '#18181b', // zinc-900
                                                        borderColor: '#27272a',    // zinc-800
                                                        borderRadius: '0.5rem',
                                                        color: '#f4f4f5'           // zinc-100
                                                    }}
                                                    itemStyle={{ color: '#06b6d4' }} // cyan-500
