import { Head } from '@inertiajs/react';
import { AnimatePresence, motion, useInView } from 'framer-motion';
import {
    ArrowRight,
    ArrowUp,
    Code,
    Github,
    Globe,
    Heart,
    Instagram,
    Linkedin,
    Mail,
    MapPin,
    Phone,
    Send,
    Terminal,
    Twitter,
